const BlogHome = () => {
  return (
    <div className="bg-gray-900 text-white py-20 px-6 font-pixel">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-3xl text-purple-300 mb-8 border-b-2 border-purple-500 inline-block pb-2">
          ðŸ“š Codex of Chronicles
        </h2>
        <div className="space-y-8 mt-10 text-left">
          {posts.map((post) => (
            <div key={post.id} className="bg-gray-800 border border-purple-600 p-4 rounded">
              <h3 className="text-lg text-purple-400">{post.title}</h3>
              <p className="text-sm text-gray-400 italic mb-2">{post.date}</p>
              <p className="text-sm text-gray-300">{post.summary}</p>
              <Link to={`/blog/${post.id}`} className="text-blue-400 hover:underline mt-2 inline-block">
                Read More â†’
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BlogHome;
