import { Link } from "react-router-dom";
import posts from "../posts";

export default function BlogHome() {
  return (
    <div className="max-w-4xl mx-auto py-10 px-4 text-white">
      <h1 className="text-4xl font-bold text-center mb-10">ðŸ“– Codex of Echoes</h1>
      <div className="grid gap-6">
        {posts.map((post) => (
          <Link to={`/blog/${post.slug}`} key={post.slug}>
            <div className="bg-[#1a1a2e] border border-purple-500 p-6 rounded-xl hover:scale-[1.02] hover:border-pink-400 transition-transform">
              <h2 className="text-2xl font-semibold">{post.title}</h2>
              <p className="text-sm text-purple-400 mb-2">{post.date}</p>
              <p className="text-gray-300">{post.description}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
